$(function(){$(".enabled_sport").on("click",function(){let e=$(this),s=e.attr("data").split("_")[1],t="/admin/sports/"+s,r=e.is(":checked");$.ajax({url:t,type:"PATCH",data:{enabled:r,id:s},success:function(e){toastr.success(e.message)},error:function(s){e.parent().hasClass("checked")?e.prop("checked",!1):e.prop("checked",!0),e.iCheck("update"),toastr.error($.parseJSON(s.responseText).error)}})})});